import "./style.css";
import  'bootstrap/dist/css/bootstrap.min.css';
function App() {

    const [toDo, setTask] = useState([]);




    let addTask = (text,date) => {
        console.log(`1add task function called !! ${text}`);
        setTask([...toDo,text]);

    }

   return (<>
   <Header heading="To Do"/>
   <Input_field addData={addTask} placeHolder="Enter Task Here!!"  btnName="Add"/>
   {toDo.map((item => <Task task={item}/>))}
   </>)
}

export default App;
