import React, { useState } from "react";
import Header from "./custom_components/Header.jsx";
import Input_field from "./custom_components/Input.jsx";
import Task from "./custom_components/Display.jsx";
import "./style.css";
import  'bootstrap/dist/css/bootstrap.min.css';
function App() {

    const [toDo, setTask] = useState([{str:"",date:""}]);



    let addTask = (text,time) => {
        console.log(`1add task function called !! ${text}`);
        setTask([...toDo,{str:text,date:time}]);

    }

   return (<>
   <Header heading="To Do"/>
   <Input_field addData={addTask} placeHolder="Enter Task Here!!"  btnName="Add"/>
   {toDo.map((item => <Task key={1} task={item}/>))}
   </>)
}

export default App;
