import React, { useState } from "react";
import Header from "./custom_components/Header.jsx";
import Input_field from "./custom_components/Input.jsx";
import Task from "./custom_components/Display.jsx";
import "./style.css";
function App() {
    const [text, setText] = useState("");
    const [toDo, setTask] = useState([]);



    let addTask = () => {
        console.log("add task function called !!");
        setTask(text);
    }
   let handle = (event) => {
    console.log("handle function called !!");
    setText(event.target.value);
    setTask(event.target.value);
   }

   return (<>
   <Header heading="To Do"/>
   <Input_field onclick={addTask} placeHolder="Enter Task Here!!" Value={text} Onchange={handle} btnName="Add"/>
   {toDo.map((item) => {
    return(<Task task={item}/>);
   })}
   </>)
}

export default App;
