function App() {
    const [text, setText] = useState("");
    const [count, setCount] = useState(0);
    let a = [];

    let addTask = () => {
        console.log("add task function called !!");
        a[count] = text;
        setCount(count++);
        console.log(count);      
    }
   let handle = (event) => {
    console.log("handle function called !!");
    setText(event.target.value);
   }

   return (<>
   <Header heading="To Do"/>
   <Input_field onclick={addTask} placeHolder="Enter Task Here!!" Value={text} Onchange={handle} btnName="Add"/>
   </>)
}

export default App;
